import Board from './components/Board'
import Turn from './components/Turn'
import Game from './components/Game'
import { useState, useEffect } from 'react'

function App() {
	const [blueTurn, changeTurn] = useState(true)
	const [gameOver, endGame] = useState(false)

	const [squares, setSquares] = useState([
	{
		row: 0,
		column: 0,
		id: "gray",
	},{
		row: 0,
		column: 1,
		id: "gray",
	},{
		row: 0,
		column: 2,
		id: "gray",
	},{
		row: 0,
		column: 3,
		id: "gray",
	},{
		row: 0,
		column: 4,
		id: "gray",
	},{
		row: 0,
		column: 5,
		id: "gray",
	},{
		row: 0,
		column: 6,
		id: "gray",
	},{
		row: 1,
		column: 0,
		id: "gray",
	},{
		row: 1,
		column: 1,
		id: "gray",
	},{
		row: 1,
		column: 2,
		id: "gray",
	},{
		row: 1,
		column: 3,
		id: "gray",
	},{
		row: 1,
		column: 4,
		id: "gray",
	},{
		row: 1,
		column: 5,
		id: "gray",
	},{
		row: 1,
		column: 6,
		id: "gray",
	},{
		row: 2,
		column: 0,
		id: "gray",
	},{
		row: 2,
		column: 1,
		id: "gray",
	},{
		row: 2,
		column: 2,
		id: "gray",
	},{
		row: 2,
		column: 3,
		id: "gray",
	},{
		row: 2,
		column: 4,
		id: "gray",
	},{
		row: 2,
		column: 5,
		id: "gray",
	},{
		row: 2,
		column: 6,
		id: "gray",
	},{
		row: 3,
		column: 0,
		id: "gray",
	},{
		row: 3,
		column: 1,
		id: "gray",
	},{
		row: 3,
		column: 2,
		id: "gray",
	},{
		row: 3,
		column: 3,
		id: "gray",
	},{
		row: 3,
		column: 4,
		id: "gray",
	},{
		row: 3,
		column: 5,
		id: "gray",
	},{
		row: 3,
		column: 6,
		id: "gray",
	},{
		row: 4,
		column: 0,
		id: "gray",
	},{
		row: 4,
		column: 1,
		id: "gray",
	},{
		row: 4,
		column: 2,
		id: "gray",
	},{
		row: 4,
		column: 3,
		id: "gray",
	},{
		row: 4,
		column: 4,
		id: "gray",
	},{
		row: 4,
		column: 5,
		id: "gray",
	},{
		row: 4,
		column: 6,
		id: "gray",
	},
	])

	const addTile = (column, turn) => {
		var i;
		var added = false;
		var newSquare = [];
		var id = "red";
		var nRow;
		if (turn){id = "blue";}
		if (!gameOver){
			for (i = squares.length-1; i >= 0; i--){
				if (added){
					newSquare.unshift(squares[i]);
				} else if (squares[i].id === "gray" && squares[i].column === column){
					nRow = squares[i].row;
					newSquare.unshift({row: nRow, column: column, id: id});
					added = true;
				} else {
					newSquare.unshift(squares[i]);
				}			
			}
		}
		setSquares(newSquare);
		if (added){
		// check game state
		console.log(id);

		//if (vertWin(column, id) || horWin(nRow, id)){
	//			endGame(true);
		//}
			changeTurn(!blueTurn);
		}
		//console.log(gameOver);
	}

	useEffect(() => {
		if (vertWin() || horWin()){
			endGame(true);
		}
	}, [squares]);

	const horWin = () => {
		var i;
		var id = "blue";
		if (blueTurn) { id = "red";}
		var count = 0;
		for (i = 0; i < 35; i++ ){
			if (i%7 === 0){ count = 0;}
			if (squares[i].id === id){
				count += 1;
				if (count >= 4){return true;}
			} else {
				count = 0;
			}
		}

		return false;
	}

	const vertWin = () => {
		var i;
		var j;
		var id = "blue";
		if (blueTurn) { id = "red";}
		var count;
		for (i = 0; i < 7; i++){
			count = 0;
			for (j = 0; j < 29; j+=7){
				if (squares[i+j].id === id){
					count += 1;
				if (count >= 4){return true;}
				} else {
					count = 0;
				}
			}
		}
		return false;
	}

	const aiguWin = (row, column, id) => {
		var i;
		var count = 0;
		for (i = row*7; i < 35; i++ ){
			if (squares[i].id === id){
				count += 1;
				if (count >= 4){return true;}
			} else {
				count = 0;
			}
		}
		return false;
	}

	const graveWin = (row, id) => {
		var i;
		var count = 0;
		for (i = row*7; i < 35; i++ ){
			if (squares[i].id === id){
				count += 1;
				if (count >= 4){return true;}
			} else {
				count = 0;
			}
		}
		return false;
	}

	return (
		<div className="container">
			<Board squares = {squares} blueTurn = {blueTurn} onAdd = {addTile} />
			<Turn blueTurn = {blueTurn}/>
			<Game gameOver = {gameOver}/>
		</div>
	);
}

export default App;
