		id: "gray",
	},{
		row: 1,
		column: 1,
		id: "gray",
	},{
		row: 1,
		column: 2,
		id: "gray",
	},{
		row: 1,
		column: 3,
		id: "gray",
	},{
		row: 1,
		column: 4,
		id: "gray",
	},{
		row: 1,
		column: 5,
		id: "gray",
	},{
		row: 1,
		column: 6,
		id: "gray",
	},{
		row: 2,
		column: 0,
		id: "gray",
	},{
		row: 2,
		column: 1,
		id: "gray",
	},{
		row: 2,
		column: 2,
		id: "gray",
	},{
		row: 2,
