		id: "gray",
	},{
		row: 2,
		column: 5,
		id: "gray",
	},{
		row: 2,
		column: 6,
		id: "gray",
	},{
		row: 3,
		column: 0,
		id: "gray",
	},{
		row: 3,
		column: 1,
		id: "gray",
	},{
		row: 3,
		column: 2,
		id: "gray",
	},{
		row: 3,
		column: 3,
		id: "gray",
	},{
		row: 3,
		column: 4,
		id: "gray",
	},{
		row: 3,
		column: 5,
		id: "gray",
	},{
		row: 3,
		column: 6,
		id: "gray",
	},{
		row: 4,
		column: 0,
		id: "gray",
	},{
		row: 4,
		column: 1,
		id: "gray",
	},{
		row: 4,
		column: 2,
		id: "gray",
	},{
		row: 4,
		column: 3,
		id: "gray",
	},{
		row: 4,
		column: 4,
		id: "gray",
	},{
		row: 4,
		column: 5,
		id: "gray",
	},{
		row: 4,
		column: 6,
		id: "gray",
	},
	])

	const addTile = (row, column, turn) => {
		var i;
		var added = false;
		var newSquare = [];
		var id = "red";
		if (turn){id = "blue";}
		for (i = squares.length-1; i >= 0; i--){
			if (added){
				newSquare.unshift(squares[i]);
			} else if (squares[i].id === "gray" && squares[i].column === column){
				newSquare.unshift({row: row, column: column, id: id});
				added = true;
			} else {
				newSquare.unshift(squares[i]);
			}

		}
		setSquares(newSquare);
		if (added){changeTurn(!blueTurn);}
	}

	return (
		<div className="container">
			<Board squares = {squares} blueTurn = {blueTurn} onAdd = {addTile}/>
			<Turn blueTurn = {blueTurn}/>
		</div>
	);
}

export default App;
