	},{
		row: 4,
		column: 5,
		id: "gray",
	},{
		row: 4,
		column: 6,
		id: "gray",
	},
	])

	const addTile = (row, column, turn) => {
		var i;
		var newSquare = [];
		var id = "red";
		if (turn){id = "blue";}
		for (i = 0; i < squares.length; i++){
			if (squares[i].row === row && squares[i].column === column){
				newSquare.push({row: row, column: column, id: id});
			} else {
				newSquare.push(squares[i]);
			}

		}
		setSquares(newSquare);
		changeTurn(!blueTurn);
	}

	return (
		<div className="container">
			<Board squares = {squares} blueTurn = {blueTurn} onAdd = {addTile}/>
			<Turn blueTurn = {blueTurn}/>
		</div>
	);
}

export default App;
